vec3 normRay = vec3(cos(s.x), sin(s.x), 1.0);
normRay /= length(normRay);
float lightHeight = 1.0;
vec3 lightRay = vec3(1.0, 1.0, lightHeight);
lightRay /= length(lightRay);
vec3 viewRay = vec3(0.0, 0.0, 1.0);
